import algoliasearch from 'algoliasearch';
const client = algoliasearch(process.env.ALGOLIA_APP_ID || '{{app_id}}', process.env.ALGOLIA_API_KEY || '{{api_key}}');
const index = client.initIndex(process.env.ALGOLIA_INDEX || '{{index}}');
await index.saveObjects([{ objectID: '1', title: 'Hello' }]);
console.log('Indexed 1 doc');
