import Anthropic from '@anthropic-ai/sdk';
const client = new Anthropic({ apiKey: process.env.ANTHROPIC_API_KEY });
const msg = await client.messages.create({ model: 'claude-3-5-sonnet-20240620', max_tokens: 128, messages: [{ role: 'user', content: 'Return {\

: \"hello\"} as JSON only.' }] });
const text = Array.isArray(msg.content) ? msg.content[0]?.text || '' : JSON.stringify(msg);
try { console.log(JSON.parse(text)); } catch { console.log(text); }
