import Anthropic from '@anthropic-ai/sdk';
const client = new Anthropic({ apiKey: process.env.ANTHROPIC_API_KEY });
const tools = [{ name: 'get_time', description: 'Get the current time', input_schema: { type: 'object', properties: {}, additionalProperties: false } }];
const msg = await client.messages.create({ model: '{{anthropic_model}}', max_tokens: 64, tools, messages: [{ role: 'user', content: 'What time is it? Use the tool.' }] });
console.log(JSON.stringify(msg, null, 2));
