import Anthropic from '@anthropic-ai/sdk';
const client = new Anthropic({ apiKey: process.env.ANTHROPIC_API_KEY });
const tools = [{ name: 'get_time', description: 'Get the current time', input_schema: { type: 'object' } }];
const msg1 = await client.messages.create({ model: '{{anthropic_model}}', max_tokens: 64, tools, messages: [{ role: 'user', content: 'What time is it? Use the tool.' }] });
console.log('step1', msg1.type);
// Conceptually send tool result back (API may differ)
const msg2 = await client.messages.create({ model: '{{anthropic_model}}', max_tokens: 64, tools, messages: [ { role: 'user', content: 'What time is it? Use the tool.' }, { role: 'tool', content: 'The time is 12:34', name: 'get_time' } ] });
console.log('step2', JSON.stringify(msg2));
