import Anthropic from '@anthropic-ai/sdk';
const client = new Anthropic({ apiKey: process.env.ANTHROPIC_API_KEY });
const msg = await client.messages.create({ model: 'claude-3-opus-20240229', max_tokens: 128, messages: [{ role: 'user', content: [{ type: 'text', text: 'Describe the image.' }, { type: 'image', source: { type: 'url', url: process.env.IMAGE_URL || '{{image_url}}' } }] }] });
console.log(JSON.stringify(msg, null, 2));
