import { gql, ApolloClient, InMemoryCache } from '@apollo/client';
const client = new ApolloClient({ uri: '/graphql', cache: new InMemoryCache() });
const MUT = gql`mutation Add($title:String!){ add(title:$title){ id title } }`;
export async function add(title: string){ await client.mutate({ mutation: MUT, variables: { title }, optimisticResponse: { add: { __typename:'Post', id: 'temp', title } } }); }
