import { ApolloClient, InMemoryCache, ApolloProvider, gql, useQuery } from '@apollo/client';
const client = new ApolloClient({ uri: '/graphql', cache: new InMemoryCache() });
function Hello(){ const { data } = useQuery(gql`query{ __typename }`); return <pre>{JSON.stringify(data)}</pre>; }
export default function App(){ return <ApolloProvider client={client}><Hello/></ApolloProvider>; }
