import { ApolloClient, InMemoryCache, HttpLink, split } from '@apollo/client';
import { getMainDefinition } from '@apollo/client/utilities';
const httpLink = new HttpLink({ uri: '/graphql' });
const altLink = new HttpLink({ uri: '/alt' });
const link = split(({ query })=> { const def = getMainDefinition(query); return def.kind==='OperationDefinition' && def.operation==='subscription'; }, altLink, httpLink);
export const client = new ApolloClient({ link, cache: new InMemoryCache() });
