import { ApolloClient, InMemoryCache, HttpLink } from '@apollo/client';
import { onError } from '@apollo/client/link/error';
import { RetryLink } from '@apollo/client/link/retry';
const errorLink = onError(({ graphQLErrors, networkError }) => { if (graphQLErrors) console.log(graphQLErrors); if (networkError) console.log(networkError); });
const retryLink = new RetryLink();
const httpLink = new HttpLink({ uri: '/graphql' });
export const client = new ApolloClient({ link: retryLink.concat(errorLink).concat(httpLink), cache: new InMemoryCache() });
