import { ApolloServer } from '@apollo/server';
import { startStandaloneServer } from '@apollo/server/standalone';
const typeDefs = `type Query { ok: Boolean! }`;
const resolvers = { Query: { ok: ()=> true } };
const server = new ApolloServer({ typeDefs, resolvers, plugins: [{ async requestDidStart(){ const start = Date.now(); return { async willSendResponse(){ console.log('duration', Date.now()-start); } }; } }] });
startStandaloneServer(server, { listen: { port: 4000 } });
