import { Database } from 'arangojs';
const db = new Database({ url: process.env.ARANGO_URL || 'http://localhost:8529' });
db.useDatabase(process.env.ARANGO_DB || '_system');
db.useBasicAuth(process.env.ARANGO_USER || 'root', process.env.ARANGO_PASS || '');
const col = db.collection('items');
await col.create(true);
await col.save({ _key: '1', name: 'Alice' });
const c = await col.count();
console.log(c.count);
