const headers = { authorization: process.env.ASSEMBLYAI_API_KEY, 'content-type': 'application/json' };
const res = await fetch('https://api.assemblyai.com/v2/transcript', { method: 'POST', headers, body: JSON.stringify({ audio_url: process.env.AUDIO_URL || '{{audio_url}}' }) });
const json = await res.json();
let id = json.id;
for (;;) {
  await new Promise(r=>setTimeout(r, 2000));
  const r = await fetch(`https://api.assemblyai.com/v2/transcript/${id}`, { headers });
  const j = await r.json();
  if (j.status === 'completed') { console.log(j.text); break; }
  if (j.status === 'error') { console.error(j.error); break; }
}
