import type { APIRoute } from 'astro';
import { z } from 'zod';
const schema = z.object({ name: z.string() });
export const post: APIRoute = async ({ request }) => { const body = await request.json(); const r = schema.safeParse(body); if(!r.success) return new Response('bad', { status: 400 }); return Response.json({ ok: true, name: r.data.name }); };
