import { GraphQLClient, gql } from 'graphql-request';
const client = new GraphQLClient(process.env.APPSYNC_URL || '{{endpoint}}', { headers: { 'x-api-key': process.env.APPSYNC_API_KEY || '{{api_key}}' } });
const q = gql`query { __typename }`;
const res = await client.request(q);
console.log(res);
