import { BatchClient, SubmitJobCommand } from '@aws-sdk/client-batch';
const c = new BatchClient({});
const r = await c.send(new SubmitJobCommand({ jobName: 'job', jobQueue: process.env.JOB_QUEUE || '{{jobQueue}}', jobDefinition: process.env.JOB_DEF || '{{jobDefinition}}', arrayProperties: { size: 10 } }));
console.log(r.jobId);
