import { BedrockRuntimeClient, InvokeModelCommand } from '@aws-sdk/client-bedrock-runtime';
const c = new BedrockRuntimeClient({});
const r = await c.send(new InvokeModelCommand({ modelId: process.env.BEDROCK_MODEL||'{{modelId}}', body: new TextEncoder().encode(JSON.stringify({ inputText: 'hello' })) }));
console.log(!!r.body);
