import { Stack } from 'aws-cdk-lib';
import * as lambda from 'aws-cdk-lib/aws-lambda';
import { Construct } from 'constructs';
export class AppStack extends Stack { constructor(scope: Construct, id: string){ super(scope, id); new lambda.Function(this, 'Fn', { runtime: lambda.Runtime.NODEJS_18_X, code: lambda.Code.fromInline('exports.handler=async()=>"ok"'), handler: 'index.handler' }); } }
