const { CloudWatchLogsClient, PutLogEventsCommand, CreateLogGroupCommand, CreateLogStreamCommand } = require('@aws-sdk/client-cloudwatch-logs');
const cwl = new CloudWatchLogsClient({});
(async ()=>{ try{ await cwl.send(new CreateLogGroupCommand({ logGroupName: '{{group}}' })); }catch{} try{ await cwl.send(new CreateLogStreamCommand({ logGroupName: '{{group}}', logStreamName: '{{stream}}' })); }catch{} const r = await cwl.send(new PutLogEventsCommand({ logGroupName: '{{group}}', logStreamName: '{{stream}}', logEvents: [{ timestamp: Date.now(), message: 'hello' }] })); console.log(Boolean(r.nextSequenceToken)); })();
