import { CloudWatchClient, PutMetricDataCommand } from '@aws-sdk/client-cloudwatch';
const cw = new CloudWatchClient({});
await cw.send(new PutMetricDataCommand({ Namespace: process.env.NS || '{{ns}}', MetricData: [{ MetricName: process.env.METRIC || '{{metric}}', Value: 1 }] }));
console.log('sent');
