import { DynamoDBClient, ExecuteStatementCommand } from '@aws-sdk/client-dynamodb';
const client = new DynamoDBClient({});
await client.send(new ExecuteStatementCommand({ Statement: `INSERT INTO {{table}} value {'id':'1','name':'Alice'}` }));
const r = await client.send(new ExecuteStatementCommand({ Statement: `SELECT * FROM {{table}}` }));
console.log(r.Items);
