import { EC2Client, StartInstancesCommand, StopInstancesCommand } from '@aws-sdk/client-ec2';
const client = new EC2Client({ region: process.env.AWS_REGION || 'us-east-1' });
if (process.argv[2] === 'start') console.log(await client.send(new StartInstancesCommand({ InstanceIds: [process.env.INSTANCE_ID || '{{instanceId}}'] })));
else console.log(await client.send(new StopInstancesCommand({ InstanceIds: [process.env.INSTANCE_ID || '{{instanceId}}'] })));
