import { ECSClient, RunTaskCommand } from '@aws-sdk/client-ecs';
const client = new ECSClient({ region: process.env.AWS_REGION || 'us-east-1' });
const out = await client.send(new RunTaskCommand({ cluster: process.env.ECS_CLUSTER || '{{cluster}}', taskDefinition: process.env.ECS_TASK || '{{taskDefinition}}', launchType: 'FARGATE', networkConfiguration: { awsvpcConfiguration: { subnets: ['subnet-123'], assignPublicIp: 'ENABLED' } } }));
console.log(out.tasks?.[0]?.taskArn);
