const { EventBridgeClient, PutEventsCommand } = require('@aws-sdk/client-eventbridge');
const eb = new EventBridgeClient({});
(async () => { const r = await eb.send(new PutEventsCommand({ Entries: [{ EventBusName: '{{bus_name}}', Source: 'app.demo', DetailType: 'demo', Detail: JSON.stringify({ hello: 'world' }) }] })); console.log(r); })();
