import { IoTDataPlaneClient, PublishCommand } from '@aws-sdk/client-iot-data-plane';
const c = new IoTDataPlaneClient({ endpoint: process.env.IOT_ENDPOINT });
try { await c.send(new PublishCommand({ topic: process.env.IOT_TOPIC || '{{topic}}', payload: new TextEncoder().encode('hello') })); console.log('published'); } catch(e){ console.log('Provide endpoint'); }
