import { KinesisClient, GetShardIteratorCommand, GetRecordsCommand } from '@aws-sdk/client-kinesis';
const c = new KinesisClient({});
const it = await c.send(new GetShardIteratorCommand({ StreamName: process.env.STREAM || '{{stream}}', ShardId: process.env.SHARD || '{{shardId}}', ShardIteratorType: 'LATEST' }));
const r = await c.send(new GetRecordsCommand({ ShardIterator: it.ShardIterator }));
console.log((r.Records||[]).length);
