const { KinesisClient, PutRecordsCommand } = require('@aws-sdk/client-kinesis');
const k = new KinesisClient({});
(async () => { const r = await k.send(new PutRecordsCommand({ StreamName: '{{stream}}', Records: [{ Data: Buffer.from('hello'), PartitionKey: 'p1' }] })); console.log(r); })();
