import { RDSDataClient, ExecuteStatementCommand } from '@aws-sdk/client-rds-data';
const c = new RDSDataClient({});
console.log(await c.send(new ExecuteStatementCommand({ resourceArn: process.env.RDS_ARN||'{{resourceArn}}', secretArn: process.env.SECRET_ARN||'{{secretArn}}', database: process.env.DB||'{{database}}', sql: 'select 1' })));
