import { Route53Client, ChangeResourceRecordSetsCommand } from '@aws-sdk/client-route-53';
const r53 = new Route53Client({});
await r53.send(new ChangeResourceRecordSetsCommand({ HostedZoneId: process.env.ZONE || '{{zoneId}}', ChangeBatch: { Changes: [{ Action: 'UPSERT', ResourceRecordSet: { Name: process.env.NAME || '{{name}}', Type: 'A', TTL: 60, ResourceRecords: [{ Value: '1.2.3.4' }] } }] } }));
console.log('upserted');
