import { S3Client } from '@aws-sdk/client-s3';
import { Upload } from '@aws-sdk/lib-storage';
const client = new S3Client({});
const u = new Upload({ client, params: { Bucket: process.env.BUCKET || '{{bucket}}', Key: process.env.KEY || '{{key}}', Body: Buffer.alloc(5*1024*1024) } });
u.on('httpUploadProgress', (p) => console.log(p));
await u.done();
