const { S3Client, PutObjectCommand } = require('@aws-sdk/client-s3');
const { getSignedUrl } = require('@aws-sdk/s3-request-presigner');
(async () => { const s3 = new S3Client({}); const url = await getSignedUrl(s3, new PutObjectCommand({ Bucket: '{{bucket}}', Key: '{{key}}' }), { expiresIn: 900 }); console.log(url); })();
