const crypto = require('crypto');
const policy = { expiration: new Date(Date.now()+3600_000).toISOString(), conditions: [{ bucket: '{{bucket}}' }, ['starts-with', '$key', ''], ['content-length-range', 0, 10485760] ] };
const policyB64 = Buffer.from(JSON.stringify(policy)).toString('base64');
// 署名はAWS認証情報から計算する必要があります（ここでは概念）。
console.log({ policy: policyB64 });
