import { SageMakerRuntimeClient, InvokeEndpointCommand } from '@aws-sdk/client-sagemaker-runtime';
const c = new SageMakerRuntimeClient({});
const r = await c.send(new InvokeEndpointCommand({ EndpointName: process.env.ENDPOINT || '{{endpoint}}', Body: new TextEncoder().encode('hello') }));
