const { SESClient, SendEmailCommand } = require('@aws-sdk/client-ses');
const ses = new SESClient({});
(async () => { const r = await ses.send(new SendEmailCommand({ Source: '{{from}}', Destination: { ToAddresses: ['{{to}}'] }, Message: { Subject: { Data: 'Hello' }, Body: { Text: { Data: 'Hello SES' } } } })); console.log(r.MessageId); })();
