import { SESClient, SendTemplatedEmailCommand } from '@aws-sdk/client-ses';
const client = new SESClient({});
await client.send(new SendTemplatedEmailCommand({ Source: 'noreply@example.com', Destination: { ToAddresses: [process.env.TO||'user@example.com'] }, Template: process.env.TEMPLATE || '{{template}}', TemplateData: JSON.stringify({ name: 'User' }) }));
console.log('sent');
