const express = require('express');
const app = express();
app.use(express.json());
app.post('/sns', (req,res)=>{ const type = req.headers['x-amz-sns-message-type']; if (type === 'SubscriptionConfirmation') { /* SubscribeURL に GET する */ console.log('confirm', req.body.SubscribeURL); } else if (type === 'Notification') { console.log('message', req.body.Message); } res.send('ok'); });
app.listen(Number('{{port}}'), ()=>console.log('listening'));
