import { SSMClient, GetParameterCommand } from '@aws-sdk/client-ssm';
const client = new SSMClient({ region: process.env.AWS_REGION || 'us-east-1' });
const out = await client.send(new GetParameterCommand({ Name: process.env.SSM_NAME || '{{name}}', WithDecryption: true }));
console.log(out.Parameter?.Value);
