const { SFNClient, StartExecutionCommand } = require('@aws-sdk/client-sfn');
const sfn = new SFNClient({});
(async () => { const r = await sfn.send(new StartExecutionCommand({ stateMachineArn: '{{state_machine_arn}}', input: JSON.stringify({ hello: 'world' }) })); console.log(r.executionArn); })();
