import { TextractClient, AnalyzeDocumentCommand } from '@aws-sdk/client-textract';
const client = new TextractClient({ region: process.env.AWS_REGION || 'us-east-1' });
const out = await client.send(new AnalyzeDocumentCommand({ Document: { S3Object: { Bucket: process.env.BUCKET || '{{bucket}}', Name: process.env.KEY || '{{key}}' } }, FeatureTypes: ['TABLES','FORMS'] }));
console.log((out.Blocks||[]).length);
