import { TextractClient, DetectDocumentTextCommand } from '@aws-sdk/client-textract';
const c = new TextractClient({});
const cmd = new DetectDocumentTextCommand({ Document: { Bytes: new Uint8Array(Buffer.from('')) } });
try { const r = await c.send(cmd); console.log(!!r.Blocks); } catch(e){ console.log('Provide real image bytes'); }
