import { TranscribeClient, StartTranscriptionJobCommand } from '@aws-sdk/client-transcribe';
const client = new TranscribeClient({ region: process.env.AWS_REGION || 'us-east-1' });
const out = await client.send(new StartTranscriptionJobCommand({ TranscriptionJobName: process.env.JOB || '{{jobName}}', Media: { MediaFileUri: process.env.URI || '{{mediaUri}}' }, LanguageCode: 'en-US' }));
console.log(out.TranscriptionJob?.TranscriptionJobName);
