const axios = require('axios');
const client = axios.create();
client.interceptors.response.use(undefined, async (err)=>{ err.config.__r=(err.config.__r||0)+1; if(err.config.__r<3) return client(err.config); throw err; });
client.get('https://example.com').then(r=>console.log(r.status));
