const { BlobServiceClient } = require('@azure/storage-blob');
const url = process.env.SOURCE_URL||'https://example.com/file.bin';
(async ()=>{ const bsc = BlobServiceClient.fromConnectionString(process.env.AZURE_STORAGE_CONNECTION_STRING||''); const container = bsc.getContainerClient('{{container}}'); await container.createIfNotExists(); const blob = container.getBlockBlobClient('{{blob}}'); const res = await blob.syncCopyFromURL(url); console.log(Boolean(res.copyId)); })();
