const { BlobSASPermissions, generateBlobSASQueryParameters, StorageSharedKeyCredential } = require('@azure/storage-blob');
const { BlobServiceClient } = require('@azure/storage-blob');
const accountName = process.env.AZURE_STORAGE_ACCOUNT||'';
const accountKey = process.env.AZURE_STORAGE_KEY||'';
const creds = new StorageSharedKeyCredential(accountName, accountKey);
const expiry = new Date(Date.now() + 3600_000);
const sas = generateBlobSASQueryParameters({ containerName: '{{container}}', blobName: '{{blob}}', permissions: BlobSASPermissions.parse('r'), expiresOn: expiry }, creds).toString();
const url = `https://${accountName}.blob.core.windows.net/{{container}}/{{blob}}?${sas}`;
console.log(url);
