import { BlobServiceClient } from '@azure/storage-blob';
const svc = BlobServiceClient.fromConnectionString(process.env.AZURE_BLOB_CS);
const c = svc.getContainerClient('public');
const b = c.getBlockBlobClient('hello.txt');
await b.uploadData(new TextEncoder().encode('hello'));
console.log('ok');
