const { ComputerVisionClient } = require('@azure/cognitiveservices-computervision');
const { CognitiveServicesCredentials } = require('@azure/ms-rest-js');
const client = new ComputerVisionClient(new CognitiveServicesCredentials(process.env.AZURE_VISION_KEY || '{{key}}'), process.env.AZURE_VISION_ENDPOINT || '{{endpoint}}');
(async () => { const res = await client.analyzeImage('https://via.placeholder.com/256', { visualFeatures: ['Tags'] }); console.log(res.tags?.map(t=>t.name)); })();
