const { MongoClient } = require('mongodb');
const uri = process.env.MONGO_URI;
(async () => { const c = new MongoClient(uri); await c.connect(); const col = c.db('db').collection('items'); await col.insertOne({ _id: '1', name: 'Alice' }); const count = await col.countDocuments(); console.log(count); await c.close(); })();
