import { CosmosClient } from '@azure/cosmos';
const c = new CosmosClient(process.env.COSMOS_CONN_STRING);
const container = c.database(process.env.COSMOS_DB || '{{db}}').container(process.env.COSMOS_CONTAINER || '{{container}}');
const { resources } = await container.items.query('select * from c').fetchAll();
console.log(resources.length);
