import { SecretClient } from '@azure/keyvault-secrets';
import { DefaultAzureCredential } from '@azure/identity';
const client = new SecretClient(process.env.VAULT_URL || '{{vaultUrl}}', new DefaultAzureCredential());
const v = await client.getSecret(process.env.SECRET_NAME || '{{secretName}}');
console.log(v.value);
