const { DefaultAzureCredential } = require('@azure/identity');
const { SecretClient } = require('@azure/keyvault-secrets');
(async () => { const client = new SecretClient('{{vault_url}}', new DefaultAzureCredential()); const r = await client.getSecret('{{secret}}'); console.log('len', (r.value||'').length); })();
