const url = `${process.env.AZURE_OPENAI_ENDPOINT || '{{endpoint}}'}/openai/deployments/${process.env.AZURE_OPENAI_DEPLOYMENT || '{{deployment}}'}/chat/completions?api-version=${process.env.AZURE_OPENAI_API_VERSION || '{{api_version}}'}`;
const res = await fetch(url, {
  method: 'POST',
  headers: { 'Content-Type': 'application/json', 'api-key': process.env.AZURE_OPENAI_API_KEY },
  body: JSON.stringify({
    messages: [{ role: 'user', content: 'Say hello' }]
  })
});
const json = await res.json();
console.log(json.choices?.[0]?.message?.content);
